One.onLoad(
    class {
        static initValidation() {
            One.helpers("jq-validation"),
                jQuery(".js-validation-signin").validate({
                    rules: {
                        "login-username": { required: !0, minlength: 4 },
                        "login-password": { required: !0, minlength: 8 },
                    },
                    messages: {
                        "login-username": {
                            required: "Lütfen kullanıcı adı girin.",
                            minlength: "Kullanıcı adınız en az 4 karakter olmalıdır.",
                        },
                        "login-password": {
                            required: "Lütfen parola girin.",
                            minlength: "Parolanız en az 8 karakter olmalıdır.",
                        },
                    },
                });
        }
        static init() {
            this.initValidation();
        }
    }.init()
);