One.onLoad(class {
    static initValidation() {
        One.helpers("jq-validation"), jQuery(".js-validation-signup").validate({
            rules: {
                "signup-username": {
                    required: true,
                    minlength: 4
                },
                "signup-referrer": {
                    required: true,
                    minlength: 6,
					maxlength: 6
                },
                "signup-password": {
                    required: true,
                    minlength: 8
                },
                "signup-password-confirm": {
                    required: true,
                    equalTo: "#signup-password"
                },
            },
            messages: {
                "signup-username": {
                    required: "Kullanıcı adı girin.",
                    minlength: "Kullanıcı adınız minimum 4 karakter olmalı."
                },
                "signup-referrer": {
                    required: "Referans numarası girin.",
                    minlength: "Geçerli bir referns numarası girin.",
					maxlength: "Referans numarası en fazla 6 hane olabilir."
                },
                "signup-password": {
                    required: "Parola Girin.",
                    minlength: "Parolanız en az 8 karakter olmalı."
                },
                "signup-password-confirm": {
                    required: "Önceki parolanın aynısını girin.",
                    minlength: "Parolanız en az 8 karakter olmalı.",
                    equalTo: "Bu parola önceki girdiğiniz parola ile eşlemiyor."
                },

            }
        })
    }
    static init() {
        this.initValidation()
    }
}.init());